function(lastChatTime){
                var rtnValue = -1;

                if (lastChatTime == 0){
                                lastChatTime = new Date().getTime();
                }

                var timeSinceLastChat = new Date().getTime() - lastChatTime;

                if (timeSinceLastChat < 60000){
                                rtnValue = 0;
                }
                else if (timeSinceLastChat < 86400000){
                                rtnValue = 32;
                }
                else if (timeSinceLastChat < 604800000){
                                rtnValue = 64;
                }
                else if (timeSinceLastChat < 2592000000){
                                rtnValue = 128;
                }
                else{
                                rtnValue = 256;
                }

                return rtnValue;
}


