/**
* Open a survey window using persistent window. On persistent chat mode this function use the persistent chat window.
* This is developed for SprintCare at this time and button with event handler need to be configured
 * in mxml skin file.
 *
 * @param webResourceID {String} The ID of the web resource to use as content for the window to display. Required.
* If null or undefined, will return with no action. (log error)
* @param winName {String} the name of the window to use in creating or referring. Required. If null or undefined,
* no action will be taken and an error will be logged to console.
* @param data {Object} Optional param for the placement of data onto the URL as parameters.
* @param winSpecData {String} Optional string param that may contain name-value pairs that specify the
* size/location etc of the window. @see http://www.w3schools.com/jsref/met_win_open.asp under "specs" for complete
* of supported specs here.
* @param custID - set via <customer-id/>
*/
function(webResourceID, winName, data, winSpecDataStr, custID) {

	if (!data) {
		log("data is required argument to call postSurveyOnPersist");
		return;
	}

	try {
		var chatID = ChatMgr.getInstance().getChatID() ;
		var o = getOpener();
		o.inqFrame["persistentChatWindow"]=null;
		o.setTimeout(Inq.closeChat, 0);
		window.onunload = null;
	} catch(err) {
		ChatMgr.getInstance().sendExitChat(chatID);
	}

	var webResource = resources[webResourceID];
	var surveyURL = buildURL(webResource.url, data, custID);

	if(!!webResource) {
		if(!!winName) {

			surveyWindow = window.parent;
			surveyWindow.document.title = winName;
			surveyWindow.window.location.href = surveyURL;
			setTimeout(function(){
				surveyWindow.window.resizeTo(512,685);
			}, 500);
			//alert("setTimeout done");

		} else {
			log("Popout Window Error: winName is required but not provided.");
		}
	} else {
		log("Popout Window Error: Web resource not found for popout window for resource ID = "+webResourceID);
	}

	/*
	* Returns URL string with given arguments.
	*
	* @param aUrl - url for action attribute.
	* @data - name and value pare used to build form data.
	* @param custID - set via <customer-id/>
	*/
	function buildURL(aURL, data, custID) {
		var urlString = aURL + "?eChat";

		for(var name in data) {
			if(typeof data[name]=="function") continue;
			urlString += "&"+name+"="+data[name];

			if (name === 'enterqueue' && data[name] === 'TODO') {
				var lineMsg = "SprintCare survey data missing: "
				+ "|chatid=" + ((CHM) ? ( (CHM.lastChat) ? CHM.lastChat.id : "-1") : "No CHM")
				+ "|customerid=" + custID
				+ "|userAgent=" + navigator.userAgent
				+ "|clientURL=" + window.top.location + " pagemarker is " + LDM.getPageMarker();
				ROM.post(urls.loggingURL, {level:"ERROR", line: lineMsg });
			}
		}
		//alert(urlString);
		return urlString;
	}
}


/**
* Non persistent window, this opens a blank window with survey URL.
* This is developed for SprintCare at this time and button with event handler need to be configured
* in mxml skin file.
*
* @param webResourceID {String} The ID of the web resource to use as content for the window to display. Required.
* If null or undefined, will return with no action. (log error)
* @param winName {String} the name of the window to use in creating or referring. Required. If null or undefined,
* no action will be taken and an error will be logged to console.
* @param data {Object} Optional param for the placement of data onto the URL as parameters.
* @param winSpecData {String} Optional string param that may contain name-value pairs that specify the
* size/location etc of the window. @see http://www.w3schools.com/jsref/met_win_open.asp under "specs" for complete
* of supported specs here.
* @param custID - set via <customer-id/>
*/
function(webResourceID, winName, data, winSpecDataStr, custID) {

	if (!data) {
		log("data is required argument to call postSurveyOnPersist");
		return;
	}

	var webResource = resources[webResourceID];
	var surveyURL = buildURL(webResource.url, data, custID);

	if(!!webResource) {
		if(!!winName) {

			try {
				surveyWindow = window.parent.open(surveyURL, winName, winSpecDataStr);
			} catch (e) {
				surveyWindow = Inq.win.open("", winName, winSpecDataStr);
			}
			if(CHM.isChatInProgress()) { }
		} else {
			log("Popout Window Error: winName is required but not provided.");
		}
	} else {
		log("Popout Window Error: Web resource not found for popout window for resource ID = "+webResourceID);
	}

	/*
	* Returns URL string with given arguments.
	*
	* @param aUrl - url for action attribute.
	* @data - name and value pare used to build form data.
	* @param custID - set via <customer-id/>
	*/
	function buildURL(aURL, data, custID) {
		var urlString = aURL + "?eChat";

		for(var name in data) {
			if(typeof data[name]=="function") continue;
			urlString += "&"+name+"="+data[name];

			if (name === 'enterqueue' && data[name] === 'TODO') {
				var lineMsg = "SprintCare survey data missing: "
				+ "|chatid=" + ((CHM) ? ( (CHM.lastChat) ? CHM.lastChat.id : "-1") : "No CHM")
				+ "|customerid=" + custID
				+ "|userAgent=" + navigator.userAgent
				+ "|clientURL=" + window.top.location + " pagemarker is " + LDM.getPageMarker();
				ROM.post(urls.loggingURL, {level:"ERROR", line: lineMsg });
			}
		}
		//console.log(urlString);
		return urlString;
	}
}


