function(){
	var dataStr ='<br/>', obj = window.sdto;
	function sdtoLoop(path, itemName) {
		var paths = path.split('.'), obj = sdto;
		for (i = 0; i < paths.length; ++i) {
			if (obj[paths[i]] != undefined) {
				obj = obj[paths[i]];
				if (typeof obj === 'string') {
					dataStr += itemName + ': ' + obj + '<br/>';
				}
			}
		}
	}

	//check if "fully authenticated before adding to datapass
	if(sdto.atg.userState == "FA") {
		sdtoLoop('atg.currentAccountUserRole', 'Role');
	}
	sdtoLoop('atg.userState', 'UserState');
	sdtoLoop('user.username', 'LoginName');
	sdtoLoop('atg.accountType', 'AcctType');
	sdtoLoop('atg.accountSubType', 'SubType');
	sdtoLoop('atg.manageSubscriberId', 'SubID');
	sdtoLoop('atg.D', 'Delq');
	sdtoLoop('page.compoundName', 'CPageName');
	sdtoLoop('navigation.clickTaleUID', 'ClickTaleUID');

	dataStr = dataStr.slice(0, -5);
	return dataStr;
}


