function(string) {
	try {
		if( typeof string !== "undefined" && string != null) {
			var temp = string;

			//Remove Site or AgentLocation from attributes list
			temp = temp.replace(/;AgentLocation.*;/g,";");
			temp = temp.replace(/;AgentLocation.*|AgentLocation.*;/g,"");
			temp = temp.replace(/;Site.*;/g,";");
			temp = temp.replace(/;Site.*|Site.*;/g,"");

			//Remove attribute name
			temp = temp.replace(/Skill=|Role=/g,"");

			//Remove/Replace specified characters
			temp = temp.replace(/ /g,"");
			temp = temp.replace(/\-|\_/g,"");
			temp = temp.replace(/\=/g,"_");
			temp = temp.replace(/\;/g,"-");
			return temp;
		} else {
			return "NA";
		}
	} catch(e) {
		return "N/A";
	}
}


