function(name, defaultValue) {
    name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
    var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),
        results = regex.exec(location.hash),
        value = results == null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
    return value || defaultValue;
}


