                var pattern = new RegExp("(^|\\s)price(\\s|$)");
                var total;

                for( i = 0; i < tags.length; i++ ){

                                if( pattern.test( tags[i].className ) && tags[i - 2].innerHTML.indexOf("Today\'s total") > -1 ){

                                                total = tags[i].innerHTML.replace(/\s/g,'').replace('<em>','').replace('</em>','');

                                                if( total.indexOf("0.00") > -1 ){
                                                                                window.ttl = "0";
                                                }
                                                else{
                                                                window.ttl = "1";                                              }
                                }
                }

                total = "";

                return {aD:total};
}

