try {
         var selTagElem = document.getElementById('radContractTerm');
         function focusFunction() {
             Inq.fireCustomEvent('radContractTermFocused');
              //console.log("radContractTerm focus");
         }
         function losefocusFunction() {
             Inq.fireCustomEvent('radContractTermUnFocused');
              //console.log("radContractTerm lose focus");
         }
         function onChangeFunction() {
             Inq.fireCustomEvent('radContractTermChangedOption');
              //console.log("radContractTerm change focus");
         }
         for (var i = 0; i < selTagElem.length; i++) {
             var xElement = selTagElem;
             // For all major browsers, except IE 8 and earlier
             if (xElement.addEventListener) {
                 xElement.addEventListener("focus", focusFunction);
                 xElement.addEventListener("change", onChangeFunction);
                 xElement.addEventListener("blur", losefocusFunction);
             }
             else if (xElement.attachEvent) {
                 // For IE 8 and earlier versions
                 xElement.attachEvent("onfocus", focusFunction);
                 xElement.attachEvent("onchange", onChangeFunction);
                 xElement.attachEvent("onblur", losefocusFunction);
             }
         }
     }
     catch (err) {
         if (window.console) //console.log("Error: " + err);
     }


